import { useState } from "react";
import { createAuthUserWithEmailAndPassword, createUserDocumentFromAuth } from "../../utils/firebase/firebase.utils";
import FormInput from "../form-input/form-input.component";
import './sign-in-form.styles.scss'
import Button from "../button/button.component";

const defaultFormFields = {
    email: "",
    password: "",
};

const SignInForm = () => {
    const [formFields, setFormFields] = useState(defaultFormFields);
    const {email, password} = formFields;

    console.log(formFields);

    const resetFormFields = () => {
        setFormFields(defaultFormFields);
    }

    const handleSubmit = async(event) => {
        event.preventDefault();
        //confirm password matches

        try {
            const {user} = await createAuthUserWithEmailAndPassword(email, password);
            await createUserDocumentFromAuth(user);
            resetFormFields();
            //const userDocRef = await createUserDocumentFromAuth(response);
        } catch(error) {
            if (error.code === 'auth/email-already-in-use') {
                alert('cannot create user email already in use')
            } else {
                console.log(error);
            }
        }


        //see if authenticated user with email password
        //create user document from return of previos

    }

    const handleChange = (event) => {
        const { name,value } = event.target;

        setFormFields({...formFields, [name]: value});
    };

    return (
        <div className="sign-up-container">
            <h2>I already have an account</h2>
            <span>Sign in with your email and password</span>
            <form onSubmit={handleSubmit}>

                <FormInput label= "Email" type="email" required onChange = {handleChange} name="email" value={email}/>

                <FormInput label= "Password" type="password" required onChange = {handleChange} name="password" value={password}/>
                <span>
                    <Button buttonType="default" type="submit">Sign In</Button>
                    <Button buttonType="google">Sign In With Google</Button>
                </span>
            </form>
        </div>
    )
}

export default SignInForm;
